### Grapheon graph drawing library
<canvas id="canvas" width="500" height="500" style='border:1px solid #CCC;clear:both;'></canvas>

	<script type="text/javascript" language="JavaScript" src="https://ajax.googleapis.com/ajax/libs/jquery/1.7.0/jquery.min.js"></script>

	<script type="text/javascript" language="JavaScript" src="js/grapheon.js"></script>
	<script type="text/javascript" language="JavaScript" src="js/grapheon.node.js"></script>
	<script type="text/javascript" language="JavaScript" src="js/grapheon.vector.js"></script>
	<script type="text/javascript" language="JavaScript" src="js/grapheon.process_algorithm1.js"></script>

	<script type="text/javascript" language="JavaScript">
		var graph;

		$(document).ready(function(){
			graph = new Grapheon({
				id:'canvas',
				canvas:{
					width:500,
					height:500
				},
				processing_algorithm: processingAlgorithm1
			});

			graph.addNode("id1","Moscow")
					.addNode("id2","Paris")
					.addNode("id3","London");

			graph.addConnection("id1","id2")
					.addConnection("id1","id3")
					.addConnection("id3","id2");

		    graph.draw().process();

		});
	</script>
